import React, {useState} from "react"
import AddCampForm from "./AddCampForm"


export default function Camping(props) {
    const {campsite, state, bathrooms, size, cost, rating, notes, _id} = props
    const [editCampingToggle, setEditCampingToggle] = useState(false)


    return( 
        <div className="camping">
            {!editCampingToggle ?
            <>
            <h1>Campsite: {campsite}</h1>
            <h2>State: {state}</h2>
            <h3>Bathrooms: {bathrooms}</h3>
            <h3>Size: {size}</h3>
            <h3>Cost per camper: {cost}</h3>
            <h3>Rating: {rating}</h3>
            <h3>Notes: {notes}</h3>
            <button
                className="delete-camp-button"
                onClick={() => props.deleteCamp}>
                    Delete
                </button>
                <button
                    className="edit-camp-button"
                    onClick={() => setEditCampingToggle(prevToggle => !prevToggle)}>
                        Edit
                    </button>
            </>
            :
            <>
                <AddCampForm
                    campsite={campsite}
                    state={state}
                    bathrooms={bathrooms}
                    size={size}
                    cost={cost}
                    rating={rating}
                    notes={notes}
                    _id={_id}
                    btnText="Submit Edit"
                    submit={props.editCamp(_id)}
                />
            <button
                onClick={() => setEditCampingToggle(prevToggle => !prevToggle)}>
                    Close
                </button>
            </>
            }
        </div>
    )
}

