import React, {useState} from "react"
import AddHikeForm from "./AddHikeForm"

export default function Hiking(props) {
    const {hikeName, distance, state, elevationGain, difficulty, notes, _id} = props
    const [editHikingToggle, setEditHikingToggle] =useState(false)

    return(
        <div className="hiking">
            { !editToggle ?
            <>
            <h1>Hike Name: {hikeName}</h1>
            <h2>State: {state}</h2>
            <h3>Distance: {distance}</h3>
            <h3>Elevation Gain: {elevationGain}</h3>
            <h3>Difficulty: {difficulty}</h3>
            <h3>Notes: {notes}</h3>
            <button     
                className="delete-hike-button"
                onClick={() => props.deleteHike(_id)}>
                    Delete
                </button>
                <button
                    className="edit-hike-button"
                    onClick={() => setEditHikingToggle(prevToggle => !prevToggle)}>
                    Edit
                </button>
            </> 
            :
            <>
                <AddHikeForm
                    hikeName={hikeName}
                    distance={distance}
                    state={state}
                    elevationGain={elevationGain}
                    difficulty={difficulty}
                    notes={notes}
                    _id={_id}
                    btnText="Submit Edit"
                    submit={props.editHike(_id)}
                />
            <button 
                onClick={() => setHikeToggle(prevToggle => !prevToggle)}>
                    Close
                </button>
            </>   
        }
        </div>
    )
}
