import React, {useState, useEffect} from "react"
import AddHikeForm from "./AddCampForm"

export default function HikeCard({props}) {
    const {campsite, state, zipcode, bathrooms, cost, difficulty, side, rating, _id, editCamp, deleteCamp, camp} = props
    const [editCampingToggle, setEditCampingToggle] =useState(false)


    useEffect(()=> { 
        setEditCampingToggle(false)
    }, [hikeName, distance, zipcode, state, elevationGain, difficulty, notes])

    return(
        <>
            <div key={_id}>
                <div className="camps-container">
                    <>
                        <h1>Name: {capsite}</h1>
                        <h2>State: {state}</h2>
                        <h3>Zipcode: {zipcode}</h3>    
                        <h4>Bathrooms: {bathrooms? "Yes" : "No"}</h4>            
                        <h4>Cost: {cost}</h4>
                        <h4>Size: {size}</h4>
                        <h4>Rating: {rating}</h4>
                        <p className="notes">Notes: {notes}</p>
                        {/* you can put edit and delete btns here */}
                    </> 
                    { editHikingToggle ?
                    <>
                        <AddHikeForm
                            key={_id}
                            hikeName={campsite}
                            state={state}
                            zipcode={zipcode}
                            bathrooms={bathrooms}
                            cost={cost}
                            size={size}
                            rating={rating}
                            notes={notes}
                            _id={_id}
                            btnText="Submit Edit"
                            submit={editHike}
                            // setEditHikingToggle={}
                        />
                        <button
                            onClick={() => setEditCampingToggle(prevToggle => !prevToggle)}>
                                Close
                            </button>
                    </> : null
                    }
                    {/* or here */}
                </div>
                <button
                    className="delete-button"
                    onClick={() => deleteCamp(_id)}>
                        Delete
                </button>
                <button
                    className="edit-button"
                    onClick={() => setEditCampingToggle(prevToggle => !prevToggle)}>
                        Edit
                </button>
            </div>

        </>       
    )
}
