                            <option>SD</option>
                            <option>TN</option>
                            <option>TX</option>
                            <option>UT</option>
                            <option>VT</option>
                            <option>VA</option>
                            <option>WA</option>
                            <option>WV</option>
                            <option>WI</option>
                            <option>WY</option>
                        </select>
                        <br></br><br></br>

                        <button className="searchBtn" onClick={showList}>Show My Recalls</button>


                        </div>
                    </div>
            {stateList}
            </div>
        </>
    )
}



export default RecallList
