import React, {useState} from 'react'
import axios from 'axios'

export default function AddCampForm(props) {
    console.log(props, "props- addCampForm")
    const initCampInputs = {
        campsite: props.campsite || "",
        state: props.state || "",
        bathrooms: props.bathrooms || "",
        size: props.size || "",
        cost: props.cost || "",
        rating: props.rating || "",
        notes: props.notes || ""
    }
    const [campInputs, setCampInputs] = useState(initCampInputs)

    function addCamp(newCamp){
        axios.post("/camping", newCamp)
        .then(res => {
          setCampInputs(prevCampInputs => [...prevCampInputs, res.data])
        })
        .catch(err => console.log(err))
      }

    function handleCampChange(e){
        const {name, value} = e.target
        setCampInputs(prevInputs => ({...prevInputs, [name]: value}))
    }

    function handleCampSubmit(e){
        e.preventDefault()
        addCamp(campInputs, props._id)
        setCampInputs(initCampInputs)
    }

    return(
        <form onSubmit={handleCampSubmit}>
            <input
                type="text"
                name="campsite"
                value={campInputs.campsite}
                onChange={handleCampChange}
                placeholder="Campsite"/>
             <input
                type="text"
                name="state"
                value={campInputs.state}
                onChange={handleCampChange}
                placeholder="State"/>
             <input
                type="text"
                name="bathrooms"
                value={campInputs.bathrooms}
                onChange={handleCampChange}
                placeholder="Bathrooms"/>
             <input
                type="text"
                name="size"
                value={campInputs.size}
                onChange={handleCampChange}
                placeholder="Camp Size"/>
             <input
                type="number"
                name="cost"
                value={campInputs.cost}
                onChange={handleCampChange}
                placeholder="Cost"/>
             <input
                type="number"
                name="rating"
                value={campInputs.rating}
                onChange={handleCampChange}
                placeholder="Rating"/>
             <input
                type="text"
                name="notes"
                value={campInputs.notes}
                onChange={handleCampChange}
                placeholder="Notes"/>
            <button>{props.btnText}</button>
        </form>
    )
}
