import React, {useState} from "react"
import AddHikeForm from "./AddHikeForm"

export default function HikeCard({props}) {
    const { distance, state, elevationGain, difficulty, notes, _id} = props

    const [editHikingToggle, setEditHikingToggle] =useState(false)


    return(
        <>
            { !editHikingToggle ?
            <>
            <button
                className="delete-button"
                onClick={() => props.deleteHike}>
                    Delete
            </button>
            <button
                className="edit-button"
                onClick={() => setEditHikingToggle(prevToggle => !prevToggle)}>
                    Edit
            </button>
            </>
            :
            <>
                <AddHikeForm
                    key={_id}
                    hikeName={hikeName}
                    distance={distance}
                    state={state}
                    elevationGain={elevationGain}
                    difficulty={difficulty}
                    notes={notes}
                    _id={_id}
                    btnText="Submit Edit"
                    submit={props.editHike}
                />
                <button
                    onClick={() => setEditHikingToggle(prevToggle => !prevToggle)}>
                        Close
                    </button>
                </>
                    }
        </>
    )
}
