import React, {useState} from 'react'
import axios from "axios";

export default function AddHikeForm(props) {
    const initHikeInputs = {
        hikeName: props.hikeName || "",
        distance: props.distance || "",
        state: props.state || "",
        elevationGain: props.elevationGain || "",
        difficulty: props.difficulty || "",
        notes: props.notes || ""
    }
    const [hikeInputs, setHikeInputs] = useState(initHikeInputs)

    console.log()
    function addHike(newHike){
        axios.post("/hiking", newHike)
        .then(res => {
          setHikeInputs(prevHikeInputs => [...prevHikeInputs, res.data])
        })
        .catch(err => console.log(err))
      }

    function handleHikeChange(e) {
        const {name, value} = e.target
        setHikeInputs(prevHikeInputs => ({...prevHikeInputs, [name]: value}))
    }

    function handleHikeSubmit(e) {
        e.preventDefault()
        addHike(hikeInputs, props._id)
        setHikeInputs(initHikeInputs)
    }

    return(
        <form onSubmit={handleHikeSubmit}>
            <input
                type= "text"
                name= "hikeName"
                value={hikeInputs.hikeName}
                onChange={handleHikeChange}
                placeholder="Hike Name" />
            <input
                type="number"
                name="distance"
                value={hikeInputs.distance}
                onChange={handleHikeChange}
                placeholder="Distance"/>
            <input
                type="text"
                name="state"
                value={hikeInputs.state}
                onChange={handleHikeChange}
                placeholder="State"/>
            <input 
                type="number"
                name="elevationGain"
                value={hikeInputs.elevationGain}
                onChange={handleHikeChange}
                placeholder="Elevation Gain"/>
            <input 
                type="number"
                name="diffiuclty"
                value={hikeInputs.diffuclty}
                onChange={handleHikeChange}
                placeholder="Difficulty"/>
            <input 
                type="text"
                name="notes"
                value={hikeInputs.notes}
                onChange={handleHikeChange}
                placeholder="Notes"/>  
            <button>{props.btnText}</button>          
        </form>
    )
}
