import React, { useState, useEffect} from "react"
import './App.css';
import Hiking from "./components/Hiking"
import Camping from './components/Camping';
import LandingPage from './components/LandingPage';
import Footer from './components/Footer';
import { BrowserRouter as Router, Routes, Route, Link} from 'react-router-dom';
import axios from "axios";


function App() {
  let windowWidth = window.innerWidth;
  const [hike, setHike] = useState([])
  const [camp, setCamp] = useState([])

  function getHike(){
    axios.get("/hiking")
    .then(res => setHike(res.data))
    .catch(err => console.log(err.response.data.errMsg))
  }

  // function addHike(newHike){
  //   axios.post("/hiking", newHike)
  //   .then(res => {
  //     setHike(prevHike => [...prevHike, res.data])
  //   })
  //   .catch(err => console.log(err))
  // }

  function deleteHike(hikingId){
    axios.delete(`/hiking/${hikingId}`)
    .then(res => {
        setHike(prevHike => prevHike.filter(hike => hike._id !== hikingId))
    })
    .catch(err => console.log(err))
}

function editHike(updates, hikeId){
  axios.put(`/hiking/${hikeId}`, updates)
  .then(res => {
      setHike(prevHike => prevHike.map(hike => hike._id !== hikeId ? hike : res.data))
  })
  .catch(err => console.log(err))
}

function handleHikeFilter(e){
  if(e.target.value === "reset"){
      getHike()
  } else {
  axios.get(`/hiking/search/difficulty?difficulty=${e.target.value}`)
  .then(res => setHike(res.data))
  .catch(err => console.log(err))
  }
}


function getCamp(){
  axios.get("/camping")
  .then(res => setCamp(res.data))
  .catch(err => console.log(err.response.data.errMsg))
}

function addCamp(newCamp){
  axios.post("/camping", newCamp)
  .then(res => {
    setCamp(prevCamp => [...prevCamp, res.data])
  })
  .catch(err => console.log(err))
}

function deleteCamp(campId){
  axios.delete(`/camping/${campId}`)
  .then(res => {
      setHike(prevCamp => prevCamp.filter(camp => camp._id !== campId))
  })
  .catch(err => console.log(err))
}

function editCamp(updates, campId){
axios.put(`/camping/${campId}`, updates)
.then(res => {
    setHike(prevCamp => prevCamp.map(camp => camp._id !== campId ? camp : res.data))
})
.catch(err => console.log(err))
}

function handleCampFilter(e){
if(e.target.value === "reset"){
    getCamp()
} else {
axios.get(`/camping/search/bathrooms?bathrooms=${e.target.value}`)
.then(res => setCamp(res.data))
.catch(err => console.log(err))
}
}

useEffect(() => {
  getHike()
  getCamp()
}, [])



  return (
    <div className="App">
      {console.log(windowWidth)}
      <Router>
        {/* <Header /> */}

        <nav className="topNavbar">

          <Link to="/" style={{padding: 5}}>Home</Link>
          <Link to="/hiking" style={{padding: 5}}>Hiking</Link>
          <Link to="/camping" style={{padding: 5}}>Camping</Link>


        </nav>

        <Routes>

          <Route path="/" element={<LandingPage />}  />
          <Route path="/hiking" element={<Hiking props={{addHike, deleteHike, editHike, handleHikeFilter}}/>} />
          <Route path="/camping" element={<Camping props={{addCamp, deleteCamp, editCamp, handleCampFilter}}/>}  />

        </Routes>

        <Footer />

      </Router>



    </div>
  );
}

export default App;
